import * as React from 'react'
import { useSpring, to, animated, config } from '@react-spring/web'
import { scale, dist } from 'vec-la'

import styles from './styles.module.css'

const predefinedPaths = {
  uTurn: [
    [0, 0],
    [100, -100],
    [200, -150],
    [300, -100],
    [350, 0],
    [300, 100],
    [200, 150],
    [100, 100],
    [0, 0]
  ] as [number, number][],
  circle: Array.from({ length: 36 }, (_, i) => {
    const angle = (i * 10 * Math.PI) / 180
    return [Math.sin(angle) * 150, Math.cos(angle) * 150] as [number, number]
  }),
  zigzag: Array.from({ length: 10 }, (_, i) => 
    [i * 50, i % 2 === 0 ? 50 : -50] as [number, number]
  )
}

export default function App() {
  const [currentPath, setCurrentPath] = React.useState('uTurn')
  const [pathIndex, setPathIndex] = React.useState(0)
  const [{ pos }, api] = useSpring(() => ({ 
    pos: [0, 0] as [number, number] 
  }))
  const [{ angle }, angleApi] = useSpring(() => ({
    angle: 0,
    config: config.wobbly,
  }))

  React.useEffect(() => {
    const path = predefinedPaths[currentPath as keyof typeof predefinedPaths]
    const interval = setInterval(() => {
      const nextIndex = (pathIndex + 1) % path.length
      setPathIndex(nextIndex)

      const currentPos = path[pathIndex]
      const nextPos = path[nextIndex]

      const direction = [nextPos[0] - currentPos[0], nextPos[1] - currentPos[1]]
      const distance = Math.sqrt(direction[0] ** 2 + direction[1] ** 2)

      if (distance > 0) {
        const normalizedDir = [direction[0] / distance, direction[1] / distance]
        angleApi.start({ angle: Math.atan2(normalizedDir[0], -normalizedDir[1]) })
      }

      api.start({
        pos: nextPos,
        config: { tension: 120, friction: 14 }
      })
    }, 500)

    return () => clearInterval(interval)
  }, [pathIndex, currentPath])

  return (
    <div>
      <div className={styles.controls}>
        <button onClick={() => { setCurrentPath('uTurn'); setPathIndex(0) }}>U-Turn</button>
        <button onClick={() => { setCurrentPath('circle'); setPathIndex(0) }}>Circle</button>
        <button onClick={() => { setCurrentPath('zigzag'); setPathIndex(0) }}>Zigzag</button>
      </div>
      <animated.div
        className={styles.rocket}
        style={{
          transform: to(
            [pos, angle],
            (posVal: [number, number], a: number) => // Explicit parameter types
              `translate3d(${posVal[0]}px,${posVal[1]}px,0) rotate(${a}rad)`
          ),
        }}
      />
    </div>
  )
}
